	{
		insert_list(first1, 0, i);
	}
	traverse(first1);
	printf("---------------------------\n");
	// merge_list(first, first1);
	traverse(first);
	reverse(first);
	printf("---------------逆置------------\n");
	traverse(first);



	return 0;
}

