 	memset(&st, 0, sizeof(struct student));
 	fread(&st, sizeof(struct student), 1, p);
 	printf("name = %s, age = %d\n", st.name, st.age);

 	fseek(p, -sizeof(struct student), SEEK_END);//从文件结尾往回偏移

 	memset(&st, 0, sizeof(struct student));
 	fread(&st, sizeof(struct student), 1, p);
